import React from 'react';
import '../App.css';

const Contact = () => {
  const handleContact = (event) => {
    event.preventDefault();
const formData = new FormData(event.target);
let name = formData.get("name");
let email = formData.get("email");
let message = document.getElementById("message");
console.log(name);
if (name == "" || email == "" || message.value == "")
{
    alert("Error: Forms left blank.")
}
else if (containsNumber(name))
{
    alert("Error: Name has a number")
}
else{
    document.getElementById("messageHandler").innerHTML = ("Hi " + name + ", Thank you for taking the time to contact me. I will contact you via " + email + " whenever I have a chance.")
}
  };
  function containsNumber(str) {
    return /[0-9]/.test(str);
  };
  return (
    <div className="App">
      <>
  <meta charSet="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
  <title>Index</title>
    <div className="topnav">
    <p> Rowan Web Programming </p>
    <a href="contact">Contact</a>
    <a href="about">About</a>
    <a href="home">Home</a>
  </div>
  <div className="hero">
    {" "}
    <p className="statement">
      {" "}
      Team #02 <br />
      <hr className="heroline"></hr>
      Contact Page <br />{" "}
    </p>{" "}
  </div>
  <div className="mainbox">
    <div className="contactbox">
    <h2> Please Contact Me </h2>
    <br />
    <p />
    <form id="contactForm" className='contactform' onSubmit={handleContact()}>
      <label htmlFor="name"> Name: </label>
      <input type="text" name="name" id="name" /> <br />
      <label htmlFor="email"> Email: </label>
      <input type="text" name="email" id="email" /> <br />
      <label htmlFor="message"> Message: </label>
      <textarea id="message" name="message" defaultValue={""} />
      <br />
      <button type="submit"> Submit </button>
    </form>
    <br />
    <h2> Contact Information </h2>
    <br />
    <p> Email Address: healyj53@students.rowan.edu </p>
    </div>
  </div>
    </>
    </div>
  )
}

export default Contact;
